insert into label (id, name, description, requires) values (DEFAULT, 'komorní', 'Štítek komorní je určen pro larpy, které se typicky hrají v jedné či dvou mísnostech, délka trvání je do 8 hodin a počet hráčů je přibližně do 20.', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'dramatický', 'Štítek dramatický je určen pro larpy, které se hrají jak ve vnitřním, tak vnějším prostředí (hrad, chata, rekreační areál, ...), vyžnačují se řízeným dějem a předem připravenými propracovanými postavami. Počet hráčů se typicky pohybuje od 20 do 120.', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'městský', 'Štítek měststký je pro larpy, které se hrají po většinu své doby ve městech.', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'bitva', 'Štítek bitva značí larpy, jejichž hlavní náplní je boj soupeřících stran. Role-playing hráčů podporuje atmosféru, ale pro hru není stěžejní. Pokud zatrhnete tento štítek, musíte dále zaškrnout štítek "dřevárna" či "airsoft(apod.)"', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'svět', 'Štítek svět je označením pro larpy, které se odehrávají často ve vnějších prostorách a na delší časové ploše (i několik dnů). Tvorba postavy jakožto i děj hry může být z velké části řízen samotnými hráči. Počet hráčů je přitom velmi různý, často se pohybuje v desítkách.', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'družinovka', 'Štítek družinovka značí larpy, kde se hráči většinu hry pohybují v menších skupinách (2-10 postav).', TRUE);
insert into label (id, name, description, requires) values (DEFAULT, 'detektivní', 'Štítek detektivní popisuje larpy, kde je jedním z hlavních cílů vyšetřování, odhalování stop, zkoumání minulosti či řešení zapeklitých situací.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'komedie', 'Štítek komedie znamená hlavně srandu, legraci či parodickou hru.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'horor', 'Štítek horor je pro larpy, kde můžete očekávat strach, překvapení či napětí.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'akční', 'Štítek akční definuje larpy, kde protáhnete své tělo - ať už během či jiným aktivním pohybem.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'sociální drama', 'Štítek sociální drama patří larpům, jejichž náplň je primárně zaměřena na mezilidské vztahy. Patří sem larpy romantické, larpy zabývající se rodinnými tématy, ale i sociálním vyloučením atp.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'fantasy', 'Štítek fantasy patří k larpům, které se odehrávají ve fikčním světě nebo světě, který je nějakým způsobem spojený s tím reálným.  Typické je pro fantasy využití magie, nadpřirozených bytostí, událostí, technologií, ras a jiných v reálném světě neexistujících jevů.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'sci-fi', 'Štítek sci-fi označuje larpy odehrávající se ve fikčním nebo našem světě. Běžným prostředím, kde se sci-fi larpy odehrávají, je budoucnost, vesmír nebo alternativní realita. Mezi časté motivy patří neobjevené nebo spekulativní technologie, neznámé formy života a přírodní jevy.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'postapo', 'Štítek postapo sedí na larpy, které se odehrávají v blízké či vzdálené budoucnosti po nějaké celospolečenské katastrofě. Mezi časté motivy patří přežití, obnova společnosti nebo shánění zdrojů.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'historický', 'Štítek historický je pro larpy, které se odehrávají v konkrétní historické době našeho světa. Hra pracuje s využitím reálných historických událostí, míst či osob.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'současnost', 'Štítek současnost zahrnuje larpy odehrávající se v dnešních reáliích (+- pár let).', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'western', 'Štítek western je pro larpy, které se odehrávají na Divokém západě nebo tomu podobných prostředích. Často se objevuje motiv dobra a zla, důraz je kladen (nikoliv nutně) na děj.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'jeepforma', 'Štítek jeepforma vyjadřuje (převážně komorní) larpy, které splňují alespoň dvě z následujících kritérií: symbolické rekvizity, střihy v čase či místě, tenké charaktery postav.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'opakovatelný', 'Štítek opakovatelný je určen pro larpy, které tvůrce tvoří již se záměrem více běhů (tedy více "stejných" her).', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'dřevárna', 'Štítek dřevárna znamená larpy, kde se k řešení konfliktů využívají dřevěné či měkčené zbraně.', FALSE);
insert into label (id, name, description, requires) values (DEFAULT, 'střelné zbraně', 'Štítek střelné zbraně prezentuje larpy, kde se k řešení konfliktů využívají airsoftové, paintballové, nerf, laserové či vodní zbraně.', FALSE);